import React, { Component } from "react";
import NavbarCSS from "../../components/header/Header.css";
import Navbar from "../../components/header/Header";
import Footer from "../../components/footer/Footer";
import Carousel from "../../components/carousel/Carousel";
import { Link } from "react-router-dom";
import axios from "axios";

function addToCart() {
  axios
    .get("http://localhost:5000/users/" + this.props.match.params.username)
    .then((response) => {
      alert("Hello");
      this.setState({
        id: response.data._id,
      });
      alert(this.state.id);
    })
    .catch(function (error) {
      console.log(error);
    });
}

const Product = (props) => (
  <div class="col-md-3">
    <div href="#" class="card card-product-grid">
      <a href="#" class="img-wrap">
        {" "}
        <img src="images/items/1.jpg" />{" "}
      </a>
      <figcaption class="info-wrap">
        <a href="#" class="title">
          {props.product.name}
        </a>
        <div class="price mt-1">{props.product.cost}</div>
        <Link
          to={"/view-product/" + props.product._id}
          className="btn btn-warning"
        >
          View
        </Link>
        <button
          to={"/view-product/" + props.product._id}
          className="btn btn-warning"
          onClick={addToCart}
        >
          Apply
        </button>
      </figcaption>
    </div>
  </div>
);

export default class HomePage extends Component {
  constructor(props) {
    super(props);

    this.state = {
      products: [],
      id: "",
    };
  }

  componentDidMount() {
    axios
      .get("http://localhost:5000/products/")
      .then((response) => {
        this.setState({ products: response.data });
      })
      .catch((error) => {
        console.log(error);
      });
  }

  productList() {
    return this.state.products.map((currentProduct) => {
      return <Product product={currentProduct} key={currentProduct._id} />;
    });
  }

  // addToCart = () => {
  //   alert("Adding to cart");
  // }

  render() {
    return (
      <div>
        <Navbar />
        <Carousel />
        <section class="section-name padding-y-sm">
          <div class="container">
            <header class="section-heading">
              <h3 class="section-title text-center my-5">Popular products</h3>
            </header>

            <div class="row">{this.productList()}</div>
          </div>
        </section>
        <Footer />
      </div>
    );
  }
}
