import React, { Component } from "react";
import axios from "axios";
import { render } from "@testing-library/react";
import NavbarCSS from "../components/header/Header.css";
import Navbar from "../components/header/Header";
export default class Login extends Component {
  constructor(props) {
    super(props);

    this.state = {
      email: "",
      password: "",
    };
  }

  formChange = (event) => {
    let name = event.target.name;
    let value = event.target.value;
    this.setState({ [name]: value });
  };

  formSubmit = (event) => {
    event.preventDefault();
    const login = {
      email: this.state.email,
      password: this.state.password,
    };
    alert(this.state.email + " " + this.state.password);
    axios
      .post("http://localhost:5000/users/login/", login)
      .then(
        (res) =>
          (window.location = "/home/" + res.data._id + "/" + res.data.email)
      )
      .catch(function (error) {
        console.log("error: " + error);
      });
  };

  render() {
    return (
      <div>
        <Navbar />
        <div
          style={{
            textAlign: "center",
            margin: "0 auto",
            border: "1px solid black",
          }}
        >
          <form onSubmit={this.formSubmit}>
            <h1>Login Form</h1>
            <p>Email ID:</p>
            <input type="text" name="email" onChange={this.formChange} />
            <p>Password:</p>
            <input type="text" name="password" onChange={this.formChange} />
            <input type="submit" />
          </form>
        </div>
      </div>
    );
  }
}
