import React, { Component } from "react";

export default class CreateProduct extends Component {
  constructor(props) {
    super(props);

    this.state = {
      name: "",
      description: "",
      cost: "",
      date: new Date(),
      vendors: [],
    };
  }

  onChangeName(e) {
    this.setState({
      name: e.target.value,
    });
  }

  onChangeDescription(e) {
    this.setState({
      name: e.target.value,
    });
  }

  onChangeCost(e) {
    this.setState({
      name: e.target.value,
    });
  }

  onChangeDate(e) {
    this.setState({
      name: e.target.value,
    });
  }

  onSubmit(e) {
    e.preventDefault();

    const product = {
      name: this.state.name,
      description: this.state.description,
      duration: this.state.duration,
      date: this.state.date,
    };

    console.log(product);
  }
  render() {
    return (
      <div>
        <p>You're on the Create Product component</p>
      </div>
    );
  }
}
