import React, { Component } from "react";
import { Link } from "react-router-dom";
import NavbarCSS from "../components/header/Header.css";
import Navbar from "../components/header/Header";
import axios from "axios";

const Product = (props) => (
  <div>
    <tr>
      <td>{props.product.name}</td>
      <td>{props.product.vendor}</td>
      <td>{props.product.description}</td>
      <td>{props.product.cost}$</td>
      <td>
        <a
          href="#"
          className="btn btn-danger"
          onClick={() => {
            props.deleteProduct(props.product._id);
          }}
        >
          Remove
        </a>
      </td>
    </tr>

    <div class="shopping-cart">
      <div class="title">Shopping Bag</div>

      <div class="item">
        <div class="buttons">
          <span class="delete-btn"></span>
          <span class="like-btn"></span>
        </div>

        <div class="image">
          <img src="item-1.png" alt="" />
        </div>

        <div class="description">
          <span>Common Projects</span>
          <span>Bball High</span>
          <span>White</span>
        </div>

        <div class="quantity">
          <button class="plus-btn" type="button" name="button">
            <img src="plus.svg" alt="" />
          </button>
          <input type="text" name="name" value="1" />
          <button class="minus-btn" type="button" name="button">
            <img src="minus.svg" alt="" />
          </button>
        </div>

        <div class="total-price">$549</div>
      </div>
    </div>
  </div>
);

export default class Cart extends Component {
  constructor(props) {
    super(props);

    this.deleteProduct = this.deleteProduct.bind(this);

    this.state = {
      products: [],
      productIds: [],
      id: this.props.match.params.userid,
      username: "",
    };
  }

  componentDidMount() {
    axios
      .get("http://localhost:5000/users/" + this.state.id)
      .then((response) => {
        this.setState({ productIds: response.data.cart });
        this.state.productIds.forEach((productId) => {
          axios
            .get("http://localhost:5000/products/" + productId)
            .then((response) => {
              this.setState({
                products: [...this.state.products, response.data],
              });
            });
        });
      })
      .catch((error) => {
        console.log(error);
      });

    axios
      .get("http://localhost:5000/users/" + this.state.id)
      .then((response) => {
        this.setState({ username: response.data.email });
        alert(this.state.username);
      })
      .catch((error) => {
        console.log(error);
      });
  }

  deleteProduct(id) {
    const cart = {
      cart: id,
    };

    axios
      .post(
        "http://localhost:5000/users/removefromcart/" +
          this.props.match.params.userid,
        cart
      )
      .then((response) => {
        console.log(response.data);
      });

    this.setState({
      products: this.state.products.filter((el) => el._id !== id),
    });
  }

  productList() {
    return this.state.products.map((currentProduct) => {
      return (
        <Product
          product={currentProduct}
          deleteProduct={this.deleteProduct}
          key={currentProduct._id}
        />
      );
    });
  }
  render() {
    return (
      <div>
        <Navbar username={this.state.username} userid={this.state.id} />
        <div className="px-5">
          <h3>Cart</h3>

          <table className="table table-striped table-bordered table-hover ">
            <thead className="thead-dark">
              <tr>
                <th>Name</th>
                <th>Vendor</th>
                <th>Description</th>
                <th>Cart</th>
                <th colSpan="2" style={{ textAlign: "center", width: "10%" }}>
                  Actions
                </th>
              </tr>
            </thead>
            <tbody>{this.productList()}</tbody>
          </table>

          <div>
            <a href="/product/create/" className="btn btn-primary">
              Add Product
            </a>
          </div>
        </div>
      </div>
    );
  }
}
