import React, { Component } from "react";
import axios from "axios";
import { render } from "@testing-library/react";
import NavbarCSS from "../components/header/Header.css";
import Navbar from "../components/header/Header";
export default class Login extends Component {
  constructor(props) {
    super(props);

    this.state = {
      email: "",
      password: "",
    };
  }

  formChange = (event) => {
    let name = event.target.name;
    let value = event.target.value;
    this.setState({ [name]: value });
  };

  formSubmit = (event) => {
    event.preventDefault();
    const login = {
      email: this.state.email,
      password: this.state.password,
    };
    alert(this.state.email + " " + this.state.password);
    axios
      .post("http://localhost:5000/users/login/", login)
      .then(
        (res) =>
          (window.location = "/home/" + res.data._id + "/" + res.data.email)
      )
      .catch(function (error) {
        console.log("error: " + error);
      });
  };

  render() {
    return (
      <div>
        <Navbar />
        <form class="form-inline">
          <div class="form-group">
            <label class="sr-only" for="email">
              Email address:
            </label>
            <input
              type="email"
              class="form-control"
              id="email"
              name="email"
              onChange={this.formChange}
            />
          </div>
          <div class="form-group">
            <label class="sr-only" for="pwd">
              Password:
            </label>
            <input
              type="password"
              class="form-control"
              id="pwd"
              name="password"
              onChange={this.formChange}
            />
          </div>
          <input type="submit" class="btn btn-default" />
        </form>

        <form class="form-horizontal" onSubmit={this.formSubmit}>
          <div class="form-group">
            <label class="control-label col-sm-2" for="email">
              Email:
            </label>
            <div class="col-sm-10">
              <input
                type="email"
                class="form-control"
                id="email"
                placeholder="Enter email"
                name="email"
                onChange={this.formChange}
              />
            </div>
          </div>
          <div class="form-group">
            <label class="control-label col-sm-2" for="pwd">
              Password:
            </label>
            <div class="col-sm-10">
              <input
                type="password"
                class="form-control"
                id="pwd"
                placeholder="Enter password"
                name="password"
                onChange={this.formChange}
              />
            </div>
          </div>
          <div class="form-group">
            <div class="col-sm-offset-2 col-sm-10">
              <button type="submit" class="btn btn-default">
                Submit
              </button>
            </div>
          </div>
        </form>
      </div>
    );
  }
}
