import React, { Component } from "react";
import NavbarCSS from "../../components/header/Header.css";
import Navbar from "../../components/header/Header";
import Footer from "../../components/footer/Footer";
import Carousel from "../../components/carousel/Carousel";

const Product = (props) => (
  <div class="col-md-3">
    <div href="#" class="card card-product-grid">
      <a href="#" class="img-wrap">
        {" "}
        <img src="images/items/1.jpg" />{" "}
      </a>
      <figcaption class="info-wrap">
        <a href="#" class="title">
          {props.product.name}
        </a>
        <div class="price mt-1">{props.product.cost}</div>
        <Link
          to={"/product/edit/" + props.product._id}
          className="btn btn-warning"
        >
          View
        </Link>
      </figcaption>
    </div>
  </div>
);

export default class HomePage extends Component {
  constructor(props) {
    super(props);

    this.deleteProduct = this.deleteProduct.bind(this);

    this.state = { products: [] };
  }

  componentDidMount() {
    axios
      .get("http://localhost:5000/products/")
      .then((response) => {
        this.setState({ products: response.data });
      })
      .catch((error) => {
        console.log(error);
      });
  }

  productList() {
    return this.state.products.map((currentProduct) => {
      return (
        <Product
          product={currentProduct}
          deleteProduct={this.deleteProduct}
          key={currentProduct._id}
        />
      );
    });
  }

  render() {
    return (
      <div>
        <Navbar />
        <Carousel />
        <section class="section-name padding-y-sm">
          <div class="container">
            <header class="section-heading">
              <h3 class="section-title text-center my-5">Popular products</h3>
            </header>

            <div class="row">
              <div class="col-md-3">
                <div href="#" class="card card-product-grid">
                  <a href="#" class="img-wrap">
                    {" "}
                    <img src="images/items/2.jpg" />{" "}
                  </a>
                  <figcaption class="info-wrap">
                    <a href="#" class="title">
                      Some item name here
                    </a>
                    <div class="price mt-1">$280.00</div>
                  </figcaption>
                </div>
              </div>
              <div class="col-md-3">
                <div href="#" class="card card-product-grid">
                  <a href="#" class="img-wrap">
                    {" "}
                    <img src="images/items/3.jpg" />{" "}
                  </a>
                  <figcaption class="info-wrap">
                    <a href="#" class="title">
                      Great product name here
                    </a>
                    <div class="price mt-1">$56.00</div>
                  </figcaption>
                </div>
              </div>
              <div class="col-md-3">
                <div href="#" class="card card-product-grid">
                  <a href="#" class="img-wrap">
                    {" "}
                    <img src="images/items/4.jpg" />{" "}
                  </a>
                  <figcaption class="info-wrap">
                    <a href="#" class="title">
                      Just another product name
                    </a>
                    <div class="price mt-1">$179.00</div>
                  </figcaption>
                </div>
              </div>
              <div class="col-md-3">
                <div href="#" class="card card-product-grid">
                  <a href="#" class="img-wrap">
                    {" "}
                    <img src="images/items/5.jpg" />{" "}
                  </a>
                  <figcaption class="info-wrap">
                    <a href="#" class="title">
                      Just another product name
                    </a>
                    <div class="price mt-1">$179.00</div>
                  </figcaption>
                </div>
              </div>
              <div class="col-md-3">
                <div href="#" class="card card-product-grid">
                  <a href="#" class="img-wrap">
                    {" "}
                    <img src="images/items/6.jpg" />{" "}
                  </a>
                  <figcaption class="info-wrap">
                    <a href="#" class="title">
                      Some item name here
                    </a>
                    <div class="price mt-1">$280.00</div>
                  </figcaption>
                </div>
              </div>
              <div class="col-md-3">
                <div href="#" class="card card-product-grid">
                  <a href="#" class="img-wrap">
                    {" "}
                    <img src="images/items/7.jpg" />{" "}
                  </a>
                  <figcaption class="info-wrap">
                    <a href="#" class="title">
                      Great product name here
                    </a>
                    <div class="price mt-1">$56.00</div>
                  </figcaption>
                </div>
              </div>
              <div class="col-md-3">
                <div href="#" class="card card-product-grid">
                  <a href="#" class="img-wrap">
                    {" "}
                    <img src="images/items/9.jpg" />{" "}
                  </a>
                  <figcaption class="info-wrap">
                    <a href="#" class="title">
                      Just another product name
                    </a>
                    <div class="price mt-1">$179.00</div>
                  </figcaption>
                </div>
              </div>
            </div>
          </div>
        </section>
        <Footer />
      </div>
    );
  }
}
