import React, { Component } from "react";
import { Link } from "react-router-dom";
import NavbarCSS from "../components/header/Header.css";
import Navbar from "../components/header/Header";
import axios from "axios";
import "../../src/components/cart-style.css";

var sum = 0;
const Product = (props) => (
  <div>
    {/* <tr>
    <td>{props.product.name}</td>
    <td>{props.product.vendor}</td>
    <td>{props.product.description}</td>
    <td>{props.product.cost}$</td>
    <td>
      <a
        href="#"
        className="btn btn-danger"
        onClick={() => {
          props.deleteProduct(props.product._id);
        }}
      >
        Remove
      </a>
    </td>
  </tr> */}

    <div class="item">
      <div class="buttons">
        <span class="button">X</span>
      </div>

      <div class="image" style={{ marginBottom: "20px" }}>
        <img src={props.product.image} width="100px" height="90px" alt="" />
      </div>

      <div class="description">
        <span>{props.product.name}</span>
        <span>{props.product.vendor}</span>
      </div>

      <div class="quantity">
        <button class="button" type="button" name="button">
          +
        </button>
        <input type="text" name="name" value="1" />
        <button class="button" type="button" name="button">
          -
        </button>
      </div>

      <div class="total-price">${props.product.cost}</div>
    </div>
  </div>
);

export default class Cart extends Component {
  constructor(props) {
    super(props);

    this.deleteProduct = this.deleteProduct.bind(this);

    this.state = {
      products: [],
      productIds: [],
      id: this.props.match.params.userid,
      username: "",
      totalCost: 0,
    };
  }

  componentDidMount() {
    axios
      .get("http://localhost:5000/users/" + this.state.id)
      .then((response) => {
        this.setState({ productIds: response.data.cart });
        this.state.productIds.forEach((productId) => {
          axios
            .get("http://localhost:5000/products/" + productId)
            .then((response) => {
              this.setState({
                totalCost: 200,
              });
              this.setState({
                products: [...this.state.products, response.data],
              });
            });
        });
        alert("Total Cost: " + this.state.totalCost);
      })
      .catch((error) => {
        console.log(error);
      });
    axios
      .get("http://localhost:5000/users/" + this.state.id)
      .then((response) => {
        this.setState({ username: response.data.email });
        alert(this.state.username);
      })
      .catch((error) => {
        console.log(error);
      });
  }

  deleteProduct(id) {
    const cart = {
      cart: id,
    };

    axios
      .post(
        "http://localhost:5000/users/removefromcart/" +
          this.props.match.params.userid,
        cart
      )
      .then((response) => {
        console.log(response.data);
      });

    this.setState({
      products: this.state.products.filter((el) => el._id !== id),
    });
  }

  productList() {
    return this.state.products.map((currentProduct) => {
      return (
        <Product
          product={currentProduct}
          deleteProduct={this.deleteProduct}
          key={currentProduct._id}
        />
      );
    });
  }
  render() {
    var sum = 0;
    this.state.products.forEach((product) => {
      sum += product.cost;
    });

    return (
      <div>
        <Navbar username={this.state.username} userid={this.state.id} />
        {/* <div className="px-5">
          <h3>Cart</h3>

          <table className="table table-striped table-bordered table-hover ">
            <thead className="thead-dark">
              <tr>
                <th>Name</th>
                <th>Vendor</th>
                <th>Description</th>
                <th>Cart</th>
                <th colSpan="2" style={{ textAlign: "center", width: "10%" }}>
                  Actions
                </th>
              </tr>
            </thead>
            <tbody>{this.productList()}</tbody>
          </table>

          <div>
            <a href="/product/create/" className="btn btn-primary">
              Add Product
            </a>
          </div>
        </div> */}

        <div class="shopping-cart">
          <div class="cart-title">Shopping Cart</div>
          {this.productList()}
          <div class="item" style={{ marginLeft: "60%" }}>
            <span>Total</span>
            <span></span>
          </div>
        </div>
      </div>
    );
  }
}
